import UIKit
import Firebase
import Photos

protocol MessageBusinessLogic
{
    func fetchMessages()
    func sendMessage(request: Message.SendMessage.Request)
    func configureDatabase()
    func configureStorage()
    func sendImage(request: Message.SendImage.Request)
    func sendQuote(request: Message.SendQuote.Request)
    func sendSchedule(request: Message.SendSchedule.Request)
}


protocol MessageDataStore
{
    var messages: [GenericMessage]! { get set }
    var conversation: Conversation! { get set }
    var yelpID: String! { get set }
}


// Types:
// 1 - text message
// 2 - image message
// 3 - schedule message
// 4 - quote message
enum MessageType {
    case Text
    case Image
    case Schedule
    case Quote
}
class MessageInteractor: MessageBusinessLogic, MessageDataStore
{
    var presenter: MessagePresentationLogic?
    var worker: MessageWorker?
    var name: String = ""
    var messages: [GenericMessage]! = []
    
    
    var ref: DatabaseReference!
    var storageRef: StorageReference!
    fileprivate var _refHandle: DatabaseHandle!
    
    var conversation: Conversation!
    var yelpID: String! = ""
    var otherID: String = ""
    
    var currentMsgIndex: Int = 0
    
    // MARK: Do something
    
    func fetchMessages() {
        self.presenter?.presentMessages(response: Message.FetchMessages.Response(userName: Auth.auth().currentUser?.displayName ?? "", businessName: conversation.name,messages: self.messages))
    }
    func sendQuote(request: Message.SendQuote.Request) {
        let msg = request.message
        var data: [String: Any] = ["quotePrice": msg.quotePrice]
        
        data["quoteDetail"] = msg.quoteDescription
        data["quoteTitle"] = ""
        data["text"] = msg.message
        data["name"] = Auth.auth().currentUser?.displayName ?? ""
        data["type"] = 4
        data["status"] = 0
        sendMessage(withData: data)
 
    }
    func sendMessage(request: Message.SendMessage.Request) {
        
        
        let data = ["text": request.message.message]
        
        var mdata = data
        mdata["name"] = Auth.auth().currentUser?.displayName ?? ""
        if let photoURL = Auth.auth().currentUser?.photoURL {
            mdata["photoURL"] = photoURL.absoluteString
        }
        sendMessage(withData: mdata)
        // Push data to Firebase Database
//        self.ref.child("messages").childByAutoId().setValue(mdata)
        
//        let msg = request.message
//        self.messages.insert(msg, at: 0)
//        let another = Message.Message(message: "Hi! Doorknob replacements typically cost around $35-50 for parts and labor. If youâ€™re available this week, I can come in to take a look at it.", sender: "Other")
//        self.messages.insert(another, at: 0)
//        self.presenter?.presentMessages(response: Message.FetchMessages.Response(userName: Auth.auth().currentUser?.displayName ?? "", businessName: conversation.name,  messages: self.messages))
    }
    func sendSchedule(request: Message.SendSchedule.Request) {
        let msg = request.message
        
        var data: [String: Any] = [:]
        
        data["text"] = msg.message
        data["name"] = Auth.auth().currentUser?.displayName ?? ""
        data["type"] = 3
        data["status"] = 0
        sendMessage(withData: data)
        
    }
    func configureDatabase() {
        ref = Database.database().reference()
        // Listen for new messages in the Firebase database
//        self.ref.chi
//        self.ref.child(<#T##pathString: String##String#>)
//        _refHandle = self.ref.child("messages")
        let user = Auth.auth().currentUser!
        var messageRef = self.ref.child("conversation-messages").child(self.conversation.conversationID)
        _refHandle = messageRef.observe(.childAdded, with: { [weak self] (snapshot) -> Void in
            guard let strongSelf = self else { return }
            let msg = snapshot.value as! NSDictionary
//            guard let msg = snapshot.value as? [String: String] else { return }
            let name: String = msg["name"] as? String ?? ""
            let text: String = msg["text"] as? String ?? ""
            let senderID : String = msg["senderID"] as? String ?? ""
            
            let msgType: Int = msg["type"] as? Int ?? 1
            var img: UIImage?
            
            if let imageURL = msg["ImageURL"] as? String {
                if imageURL.hasPrefix("gs://") {
                    let temp = strongSelf.messages.count
                    Storage.storage().reference(forURL: imageURL).getData(maxSize: INT64_MAX) {(data, error) in
                        if let error = error {
                            print("Error downloading: \(error)")
                            return
                        }
                        img = UIImage(data: data!)
                        var message: GenericMessage
                        if img != nil {
                            message = Message.ImageMessage(message: "", sender: senderID , image: img)
                        } else {
                            message = Message.Message(message: text, sender: senderID)
                        }
                        
                        let insertIndex = strongSelf.messages.count - temp
                        strongSelf.messages.insert(message, at: insertIndex)
                        strongSelf.presenter?.presentMessages(response: Message.FetchMessages.Response(userName: Auth.auth().currentUser?.displayName ?? "", businessName: strongSelf.conversation.name,messages: strongSelf.messages))
                        
                    }
                } else if let URL = URL(string: imageURL), let data = try? Data(contentsOf: URL) {
                    img = UIImage.init(data: data)
                    var message: GenericMessage
                    if img != nil {
                        message = Message.ImageMessage(message: "", sender: senderID, image: img)
                    } else {
                        message = Message.Message(message: text, sender: senderID)
                    }
                    
                    
                    strongSelf.messages.insert(message, at: 0)
                    strongSelf.presenter?.presentMessages(response: Message.FetchMessages.Response(userName: Auth.auth().currentUser?.displayName ?? "", businessName: strongSelf.conversation.name,messages: strongSelf.messages))
                }
//                cell.textLabel?.text = "sent by: \(name)"
            } else {
                var message: GenericMessage
                if msgType == 4 {
                    let quote = msg["quotePrice"] as! Double
                    let desc = msg["quoteDetail"] as! String
                    
                    message = Message.QuoteMessage(message: "", sender: senderID, quotePrice: quote, quoteDescription: "", messageID: snapshot.key)
                } else {
                    if img != nil {
                        message = Message.ImageMessage(message: "", sender: senderID, image: img)
                    } else if msgType == 3 {
                        let dur = (msg["duration"] as? Int ) ?? 0
                        let dates = (msg["dates"] as? [Double]) ?? []
                        let title = (msg["title"] as? String) ?? ""
                        message = Message.ScheduleMessage(message: "", sender: senderID, duration: dur, title: title, availabilities: dates)
//                        message = Message.ScheduleMessage(message: "", sender: senderID, availabilities: [])
                    } else {
                        message = Message.Message(message: text, sender: senderID)
                    }
                }
                
                
                
                
                strongSelf.messages.insert(message, at: 0)
                strongSelf.presenter?.presentMessages(response: Message.FetchMessages.Response(userName: Auth.auth().currentUser?.displayName ?? "", businessName: strongSelf.conversation.name,messages: strongSelf.messages))
            }
            
//            strongSelf.clientTable.insertRows(at: [IndexPath(row: strongSelf.messages.count-1, section: 0)], with: .automatic)
        })
    }
    func configureStorage() {
        storageRef = Storage.storage().reference()
    }
    deinit {
        if let refHandle = _refHandle {
            self.ref.child("messages").removeObserver(withHandle: _refHandle)
        }
    }
    func sendMessage(withData data: [String: Any]) {
        var mdata = data
        let user = Auth.auth().currentUser!
        mdata["senderID"] = user.uid
        mdata["name"] = user.displayName ?? ""
        if mdata["type"] == nil {
            mdata["type"] = 1
        }
        if let photoURL = Auth.auth().currentUser?.photoURL {
            mdata["photoURL"] = photoURL.absoluteString
        }
        mdata["timestamp"] = Date().timeIntervalSince1970
        
        
        
        // Push data to Firebase Database
        var messageRef = self.ref.child("conversation-messages").child(self.conversation.conversationID)
        var newRef = messageRef.childByAutoId()
        newRef.setValue(mdata)
        
        if mdata["type"]! as! Int == 3 { // schedule
            self.ref.child("conversation-data").setValue(["scheduleID": newRef.key])

            self.ref.child("users").child(user.uid).child("conversations").child(self.conversation.conversationID).setValue(["scheduleID": newRef.key])
            self.ref.child("users").child(self.conversation.contractorID).child("conversations").child(self.conversation.conversationID).setValue(["scheduleID": newRef.key])
            
        } else if mdata["type"]! as! Int == 4  { // quote
            self.ref.child("conversation-data").setValue(["quoteID": newRef.key])
            self.ref.child("users").child(user.uid).child("conversations").child(self.conversation.conversationID).setValue(["quoteID": newRef.key])
            self.ref.child("users").child(self.conversation.contractorID).child("conversations").child(self.conversation.conversationID).setValue(["quoteID": newRef.key])
        }
        
        
        //        self.clientTable.scrollToRow(at: <#T##IndexPath#>, at: <#T##UITableViewScrollPosition#>, animated: <#T##Bool#>)
    }
    func sendImage(request: Message.SendImage.Request) {
        let info = request.info
        let uid = Auth.auth().currentUser!.uid
        // if it's a photo from the library, not an image from the camera
        if #available(iOS 8.0, *), let referenceURL = info[UIImagePickerControllerReferenceURL] as? URL {
            let assets = PHAsset.fetchAssets(withALAssetURLs: [referenceURL], options: nil)
            let asset = assets.firstObject
            asset?.requestContentEditingInput(with: nil, completionHandler: { [weak self] (contentEditingInput, info) in
                let imageFile = contentEditingInput?.fullSizeImageURL
                let filePath = "\(uid)/\(Int(Date.timeIntervalSinceReferenceDate * 1000))/\((referenceURL as AnyObject).lastPathComponent!)"
                guard let strongSelf = self else { return }
                strongSelf.storageRef.child(filePath)
                    .putFile(from: imageFile!, metadata: nil) { (metadata, error) in
                        if let error = error {
                            let nsError = error as NSError
                            print("Error uploading: \(nsError.localizedDescription)")
                            return
                        }
                        strongSelf.sendMessage(withData: ["type": 2, "ImageURL": strongSelf.storageRef.child((metadata?.path)!).description])
//                        mdata["type"] = 1
                }
            })
        } else {
            guard let image = info[UIImagePickerControllerOriginalImage] as? UIImage else { return }
            let imageData = UIImageJPEGRepresentation(image, 0.8)
            let imagePath = "\(uid)/\(Int(Date.timeIntervalSinceReferenceDate * 1000)).jpg"
            let metadata = StorageMetadata()
            metadata.contentType = "image/jpeg"
            self.storageRef.child(imagePath)
                .putData(imageData!, metadata: metadata) { [weak self] (metadata, error) in
                    if let error = error {
                        print("Error uploading: \(error)")
                        return
                    }
                    guard let strongSelf = self else { return }
                    strongSelf.sendMessage(withData: ["type": 2, "ImageURL": strongSelf.storageRef.child((metadata?.path)!).description])
            }
        }
    }
        
    
}
